void LED_Blink(void* arg)
{
	for(;;)
	{
		if(GPIO_ReadPin(GPIOA,0U))
		{
			delay(20);								//Debounce

			GPIO_WritePin(GPIOC,13u,HIGH);			//LED off
		}
		else
		{
			delay(20);								//Debounce

			for(uint32_t i=0; i<50; i++)			//LED Low intensity on (similar to PWM)
			{
				GPIO_WritePin(GPIOC,13u,HIGH);
				delay(20);
				GPIO_WritePin(GPIOC,13u,LOW);
				delay(1);
			}

			GPIO_WritePin(GPIOC,13u,HIGH);			//LED off
			delay(500);
		}
	}
}
